import React from 'react';

class ListarCurso extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            id:"",
            nombre:"",
            descripcion:"",
            tiempo:"",
            usuario:"",
            url:"https://paginas-web-cr.com/ApiPHP/apis/",
            listar:"ListaCurso.php"

        }
    }



    cargardatos(){
       var urllista = this.state.url + this.state.listar;
       const {url} = this.state;
       console.log(url);
       fetch (urllista)
       .then((respuesta) => respuesta.json())
       .then((datosrespuesta) => {
       
       
            this.setState({
                id: datosrespuesta.id,
                nombre: datosrespuesta.nombre,
                descripcion: datosrespuesta.descripcion,
                tiempo: datosrespuesta.tiempo,
                usuario : datosrespuesta.usuario
            });
        
        console.log(datosrespuesta.data)
       })
        
       .catch(console.log);
    
    }

    componentDidMount(){
        this.cargardatos();
    }

    
    render() { 
        const  {
            id,
            nombre,
            descripcion,
            tiempo,
            usuario,
            url,
            listar
        } = this.state;

        return ( 
            
            <><div className="container">
                <h1>Listar Curso</h1>
            </div><div className="table-responsive">
                    <table className="table table-success">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Nombre</th>
                                <th scope="col">Descripcion</th>
                                <th scope="col">Tiempo</th>
                                <th scope="col">Usuario</th>
                            </tr>
                        </thead>
                        <tbody>
                           
                           
                           
                           
                           
                            <tr className="">

                                <td scope="row">{id}</td>
                                <td>{nombre}</td>
                                <td>{descripcion}</td>
                                <td>{tiempo}</td>
                                <td>{usuario}</td>
                            </tr>
                            
                        </tbody>
                    </table>
                </div></>
            
            );
    }
}
 
export default ListarCurso;
