import React from 'react';

class ListarCurso extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            id:"",
            nombre:"",
            descripcion:"",
            tiempo:"",
            usuario:"",
            url:"https://paginas-web-cr.com/ApiPHP/apis/",
            listar:"ListaCurso.php"

        }
        let tablaresultado = document.querySelector('#tablaresultado');
    }




    cargardatos(){
       var urllista = this.state.url + this.state.listar;
       const {url} = this.state;
       console.log(url);
       fetch (urllista)
       .then((respuesta) => respuesta.json())
       .then((datosrespuesta) => {
       
        for (const objetoindividual of datosrespuesta.data){
            this.tablaresultado.innerHTML += `
           
            <tr class="table-success text-align-center" >
            <td scope="row">${objetoindividual.id}</td>
            <td>${objetoindividual.nombre}</td>
            <td>${objetoindividual.descripcion}</td>
            <td>${objetoindividual.tiempo}</td>
            <td>${objetoindividual.usuario}</td>`;
        }
        console.log(datosrespuesta.data)
       })
        
       .catch(console.log);
    
    }

    componentDidMount(){
        this.cargardatos();
    }

    
    render() { 
        const  {
            id,
            nombre,
            descripcion,
            tiempo,
            usuario,
            url,
            listar
        } = this.state;

        return ( 
            
            <><div className="container">
                <h1>Listar Curso</h1>
            </div><div className="table-responsive">
                    <table className="table table-success">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Nombre</th>
                                <th scope="col">Descripcion</th>
                                <th scope="col">Tiempo</th>
                                <th scope="col">Usuario</th>
                            </tr>
                        </thead>
                        <tbody class="table-group-divider table-success textalign-center" id="tablaresultado">
                       
                            
                        </tbody>
                    </table>
                </div></>
            
            );
    }
}
 
export default ListarCurso;
